sourcesContent:
'use strict';
const { Model } = require('sequelize');

module.exports = (sequelize, DataTypes) => {
  class Friendship extends Model {
    static associate(models) {

      // ngi gi li mi
      Friendship.belongsTo(models.User, {
        foreignKey: 'user_id',
        as: 'sender'
      });

      // ngi nhn li mi
      Friendship.belongsTo(models.User, {
        foreignKey: 'friend_id',
        as: 'receiver'
      });
    }
  }

  Friendship.init({
    user_id: {
      type: DataTypes.INTEGER,
      allowNull: false
    },
    friend_id: {
      type: DataTypes.INTEGER,
      allowNull: false
    },
    status: {
      type: DataTypes.INTEGER, //  nn l INTEGER
      allowNull: false
    }
  }, {
    sequelize,
    modelName: 'Friendship',
    tableName: 'friendships',
    underscored: true,
    timestamps: false
  });

  return Friendship;
};

